import React from 'react';
import './MainStyles.css';
import { useState } from 'react';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import {IconButton} from "@mui/material";
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import GroupAddIcon from '@mui/icons-material/GroupAdd';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import NightlightIcon from '@mui/icons-material/Nightlight';
import SearchIcon from '@mui/icons-material/Search';
import ConversationItems from "./ConversationItems.js";
import { useNavigate } from 'react-router-dom';






const Sidebar = () => {
  const [conversations,setConversations]=useState([
    {
      name: "test1",
      lastMessage: "Last Message #1",
      timestamp: "today"
    },
    {
      name: "test2",
      lastMessage: "Last Message #2",
      timestamp: "today"

    },
    {
      name: "test3",
      lastMessage: "Last Message #2",
      timestamp: "today"
    },

  ]);
   const navigate = useNavigate();
  return (
    <div className = "sidebar-container">
     <div className="sb-header">
     <div>
     <IconButton>
     <AccountCircleIcon />
     </IconButton>
     </div>
     
    <div>
    <IconButton onClick={() => {navigate("users")}}>
      <PersonAddIcon />
     </IconButton>
     <IconButton onClick={() => {navigate("groups")}}>
       <GroupAddIcon />
     </IconButton>

     <IconButton onClick={() => {navigate("create-groups")}}>
       <AddCircleIcon />
     </IconButton>
     <IconButton>
      <NightlightIcon />
     </IconButton>

    </div>
     
  
     </div>
   
     <div className="sb-search">
     <IconButton>
     <SearchIcon />

     </IconButton>
      
      <input placeholder='Search'  className="search-box"/>
        
     </div>
     <div className="sb-conversation">
     {console.log("length",conversations.length)}
     {/* <h1>hi</h1> */}
      {/* <ConversationItems ele={conversation} key={conversation.name} />
     {conversations.map((conversation) => {
      
      return
      (
        <p>hello</p>
      
      )
     })

     } */}
     {conversations.map((conversation)=>{
      return <ConversationItems ele={conversation} key={conversation.name} />
     })}
     
     </div>
    </div>
  )
}

export default Sidebar;
